import React, { Component} from 'react'
import DriveForm from './DriveForm'
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup'
import ToggleButton from 'react-bootstrap/ToggleButton'
import { Container,Nav } from 'react-bootstrap';

class NewDrive extends Component{

    state = {
      subscription:false,
      location:'',
      distinatin:'', 
      date:'',
      hour:'',
      stops:'',
      carId: '',
      carType:'',
      seatsNum:'',
      remarks:'',
      value: [],
      exeption:'',
      }


      handleChangeTab = (e,id) => {
          console.log("Sssssssssssss");
        if(e.target.id==='single'){
        this.setState({
          subscription: false
        })
      }
        else{
          this.setState({
            subscription: true
          })
        }
        console.log(this.state);
      }
      handleSubmit = (e) => {
        e.preventDefault();
        
        console.log(this.state);
      }


      handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value
        })
        console.log(this.state);
      }
      handleSubmit = (e) => {
        e.preventDefault();
        
        console.log(this.state);
      }
      handleChangeTog=(value, e) =>{
        this.setState({
         value: value
        });
        console.log(this.state);
      }
   
    render() { 
          
       if (this.state.subscription){
            return (
                
                <div>
                <Nav variant="tabs" defaultActiveKey="/new_Drive" >
                <Nav.Item>
                  <Nav.Link  id='single' onSelect={this.handleChangeTab}>Single Ride</Nav.Link >
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link  id='sub' onSelect={this.handleChangeTab}>Subscription</Nav.Link>
                </Nav.Item>
              </Nav>
                <form className="form" onSubmit={this.handleSubmit}>
                    <DriveForm/>
                <Container>
                    <h6> I'll do this ride every: </h6>
                    <ToggleButtonGroup
                        type="checkbox"
                        value={this.state.value}
                        onChange={this.handleChangeTog}>
                        <ToggleButton value={1}>Su</ToggleButton>
                        <ToggleButton value={2}>Mo</ToggleButton>
                        <ToggleButton value={3}>Tu</ToggleButton>
                        <ToggleButton value={4}>We</ToggleButton>
                        <ToggleButton value={5}>Th</ToggleButton>
                        <ToggleButton value={6}>Fr</ToggleButton>
                        <ToggleButton value={7}>Sa</ToggleButton>
                    </ToggleButtonGroup>
                     
                        <div className="input-field">
                            <label htmlFor="exeption">Date Exeption </label>
                            <input type="text" id='exeption' onChange={this.handleChange} />
                        </div>
                        <h6>   </h6>
                        <div className="input-field">
                            <button className="btn signBtn ">Add Subscription</button>
                        </div>
                        </Container>
                    </form>
                        </div>
                
                
                );
            }
        else{
          return(
            <div>
            <Nav variant="tabs" defaultActiveKey="/new_Drive" >
                <Nav.Item>
                  <Nav.Link  id='single' onChange={this.handleChangeTab}>Single Ride</Nav.Link >
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link  id='sub' onChange={this.handleChangeTab}>Subscription</Nav.Link>
                </Nav.Item>
              </Nav>
             <DriveForm/>;
             <div className="input-field">
                 <button className="btn signBtn ">Add Drive</button>
            </div>
        </div>
        )
        }
    }

    }
 
    
export default NewDrive
