import React, { Component} from 'react'
import { Container, Nav } from 'react-bootstrap';

class DriveForm extends Component{

    state = {
      subscription: false,
      location:'',
      distinatin:'', 
      date:'',
      hour:'',
      stops:'',
      carId: '',
      carType:'',
      seatsNum:'',
      remarks:'',
      tab:''
      }
      handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value
        })
        console.log(this.state);
      }
      handleChangeTab = (e,id) => {
        if(e.id==='single'){
        this.setState({
          subscription: false
        })
      }
        else{
          this.setState({
            subscription: true
          })
        }
        console.log(this.state);
      }
      handleSubmit = (e) => {
        e.preventDefault();
        
        console.log(this.state);
      }

    render() {
       
      return (
        <Container>
              <Nav variant="tabs" defaultActiveKey="/new_Drive" onCange={this.handleChangeTab}>
                <Nav.Item>
                  <Nav.Link href="/new_Drive"  id='single'>Single Ride</Nav.Link >
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link eventKey="link-1" id='sub'>Subscription</Nav.Link>
                </Nav.Item>
              </Nav>
              <form className="form" onSubmit={this.handleSubmit}>
                <div className="input-field">
                    <label htmlFor="location">Location</label>
                    <input type="text" id='location' onChange={this.handleChange} />
                </div>
                <div className="input-field">
                    <label htmlFor="distination">Distination</label>
                    <input type="text" id='distination' onChange={this.handleChange} />
                </div>
                <div className="input-field">
                    <label htmlFor="date">Date</label>
                    <input type="datetime-local" id='date' onChange={this.handleChange} />
                </div>
                <div className="input-field">
                    <label htmlFor="carId">Car Id</label>
                    <input type="text" id='carId' onChange={this.handleChange} />
                </div>
                <div className="input-field">
                    <label htmlFor="carType">car Type</label>
                    <input type="text" id='carType' onChange={this.handleChange} />
                </div>
                <div className="input-field">
                    <label htmlFor="seatsNum">Number of seats</label>
                    <input type="text" id='seatsNum' onChange={this.handleChange} />
                </div>
                <div className="input-field">
                    <label htmlFor="remarks">Remarks</label>
                    <input type="text" id='remarks' onChange={this.handleChange} />
                </div>
                
               x  
        </form>
        </Container>
        ) 
    
    }
}
export default DriveForm
