import React, { Component} from 'react'
import DriveForm from './DriveForm'
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup'
import ToggleButton from 'react-bootstrap/ToggleButton'
class NewDrive extends Component{

    state = {
      subscription: false,
      location:'',
      distinatin:'', 
      date:'',
      hour:'',
      stops:'',
      carId: '',
      carType:'',
      seatsNum:'',
      remarks:'',
      value: [],
      }


      handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value
        })
        console.log(this.state);
      }
      handleSubmit = (e) => {
        e.preventDefault();
        
        console.log(this.state);
      }
   
  
    render() {
       if (this.state.subscription){
            return (
                <div>
                <form className="form" onSubmit={this.handleSubmit}>
                    <DriveForm/>
                    <ToggleButtonGroup
                        type="checkbox"
                        value={this.state.value}
                        onChange={this.handleChange}
                    >
                        <ToggleButton value={1}>Su</ToggleButton>
                        <ToggleButton value={2}>Mo</ToggleButton>
                        <ToggleButton value={3}>Tu</ToggleButton>
                        <ToggleButton value={4}>We</ToggleButton>
                        <ToggleButton value={5}>Th</ToggleButton>
                        <ToggleButton value={6}>Fr</ToggleButton>
                        <ToggleButton value={7}>Sa</ToggleButton>
                    </ToggleButtonGroup>
                        <div className="input-field">
                            <label htmlFor="remarks">Remarks</label>
                            <input type="text" id='remarks' onChange={this.handleChange} />
                        </div>
                        
                        <div className="input-field">
                            <button className="btn signBtn ">Add Drive</button>
                        </div>
                    </form>
                        </div>
                
                );
            }
        else{return(
        <div> 
             <DriveForm/>;
             <div className="input-field">
                            <button className="btn signBtn ">Add Drive</button>
                </div>
        </div>
        )
        }
    }

    }
 
    
export default NewDrive
