import React, { Component} from 'react'
import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form';
import { Row, Col } from 'react-bootstrap';


class SearchBar extends Component{

    state = {
        location: '',
        distination: '',
        time:''
      }
      handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value
        })
      }
      handleSubmit = (e) => {
        e.preventDefault();
        this.props.signIn(this.state)
      }
    
    render(){
    return (
            
      <Container className="timef "> 
            <Form className="search" > 
            <Row noGutters={true}>
                <Col>
             <Form.Group controlId="location" onChange={this.handleChange}>
                <Form.Label>To</Form.Label>
                <Form.Control as="textarea" rows="1" />
             </Form.Group>
             </Col>
             <Col>
             <Form.Group controlId="distination" onChange={this.handleChange}>
                <Form.Label>From</Form.Label>
                <Form.Control as="textarea" rows="1" />
             </Form.Group>
             </Col>
             </Row>
             <Row> 
       
             <Col>
             <Form.Group controlId="time" onChange={this.handleChange}>
             <Form.Label>At</Form.Label>
             <Form.Control type="datetime-local" rows="1" className=" white rounded size" />
            
             </Form.Group> 
                
             </Col>
             </Row>
            </Form>
            </Container>        
            
        )
    }
}
export default SearchBar
